import { useState } from "react";
import axios from "axios";

export default function Contact() {
  const [form, setForm] = useState({ name: "", email: "", message: "" });
  const [status, setStatus] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:8080/api/contact", form);
      setStatus("✅ Message sent successfully!");
      setForm({ name: "", email: "", message: "" });
    } catch (err) {
      console.error("❌ Error sending message:", err);
      setStatus("❌ Failed to send message.");
    }
  };

  return (
    <section className="py-16 bg-white text-center">
      <h2 className="text-3xl font-bold text-purple-700 mb-6">Contact Me</h2>
      <form onSubmit={handleSubmit} className="max-w-md mx-auto flex flex-col gap-3">
        <input
          className="border p-3 rounded"
          placeholder="Your Name"
          value={form.name}
          onChange={(e) => setForm({ ...form, name: e.target.value })}
        />
        <input
          className="border p-3 rounded"
          placeholder="Your Email"
          value={form.email}
          onChange={(e) => setForm({ ...form, email: e.target.value })}
        />
        <textarea
          className="border p-3 rounded"
          rows="4"
          placeholder="Message"
          value={form.message}
          onChange={(e) => setForm({ ...form, message: e.target.value })}
        />
        <button className="bg-purple-600 text-white py-2 rounded hover:bg-purple-700">
          Send
        </button>
        {status && <p className="text-green-600 mt-2">{status}</p>}
      </form>
    </section>
  );
}
