export default function About() {
  return (
    <section className="py-16 bg-white text-center">
      <h2 className="text-3xl font-bold text-purple-700 mb-6">About Me</h2>
      <p className="max-w-3xl mx-auto text-gray-600">
        I'm a 3rd-year Software Engineering student at Dong A University.
        I enjoy creating modern apps, working with IoT devices, and learning DevOps tools.
      </p>
    </section>
  );
}
